import ReactDOM from 'react-dom/client';
import './index.css';
// import App from './App';
import { Component } from 'react';
import { useState } from 'react';

const initialTools = [
  {
    id:1,
    title:"todo 1",
    complete:false
  },
  {
    id:2,
    title:"todo 2",
    complete:false
  }
]

const reducer = (state, action) => {
  switch(action, type) {
    case "complete":
      return state.map((todo) => {
        if(todo.id == action.id){
          return {...todo, complete: !todo.complete}
        } else{
          return
        }
      })
  }
}

